library(rvest)

scrape_team_func <- function(x, nationality){
  
  x <- read_html(x)
  
# Players ID
id <- x %>%
  html_nodes('.col-pi') %>%
  html_text()

#Overall Rating
rating <- x %>%
  html_nodes('.col-sort') %>%
  html_text()
  

# Player's Names
name <- x %>%
  html_nodes('.persist-header:nth-child(1) .col-avatar+ .text-left , .col-name .tooltip .bp3-text-overflow-ellipsis') %>%
  html_text()

# Players Height
height <- x %>%
  html_nodes('.col-hi') %>%
  html_text()

# Overall Rating 
rating <- x %>%
  html_nodes('.col-bo') %>%
  html_text()

# Best Position 
best_position <- x %>%
  html_nodes('.col-bp') %>%
  html_text()

# Value 
value <-x %>%
  html_nodes('.col-vl') %>%
  html_text()

# Foot 
foot <-x %>%
  html_nodes('.col-pf') %>%
  html_text()

# Attacking 
attacking <-x %>%
  html_nodes('.col-ta') %>%
  html_text()

# Crossing
crossing <-x %>%
  html_nodes('.col-cr') %>%
  html_text()

# Finishing
finishing <-x %>%
  html_nodes('.col-fi') %>%
  html_text()

# Short Passing 
short_passing <- x %>%
  html_nodes('.col-sh') %>%
  html_text()

# Total Skill
skill <- x %>%
  html_nodes('.col-ts') %>%
  html_text()

# Dribbling
dribbling <- x %>%
  html_nodes('.col-dr') %>%
  html_text()

# Long Passing
long_passing <- x %>%
  html_nodes('.col-lo') %>%
  html_text()

# Ball Control
control <- x %>%
  html_nodes('.col-bl') %>%
  html_text()

# Total Defense
defense <- x %>%
  html_nodes('.col-td') %>%
  html_text()

# Total Goal Keep
total_goalkeeping <- x %>%
  html_nodes('.col-tg') %>%
  html_text()

# Creating a dataframe with all the info from players
table <- tibble(id=id, 
                rating=rating,
                height=height,
                name=name,
                rating=rating,
                best_position=best_position,
                value=value, 
                foot=foot, 
                attacking=attacking, 
                crossing=crossing, 
                finishing=finishing, 
                short_passing=short_passing, 
                skill=skill, 
                dribbling=dribbling, 
                long_passing=long_passing, 
                control=control, 
                defense=defense, 
                total_goalkeeping=total_goalkeeping
) %>% mutate(nationality)

# Deleting the first row because it extracts the name of the column. 
table <- table[-1,]
}


